import { EshopActions } from '../actions';
export const initialState = {
  orders: null,
  order: null,
  productImages: [],
  translations: [],
  allProducts: [],
  allCategories: []
};
export function dashboardReducer(state = initialState, action) {
  switch (action.type) {
    case EshopActions.GetOrdersSuccess:
      {
        return { ...state,
          orders: action.payload
        };
      }

    case EshopActions.GetOrderSuccess:
      {
        return { ...state,
          order: action.payload
        };
      }

    case EshopActions.GetImagesSuccess:
      {
        return { ...state,
          productImages: action.payload.all
        };
      }

    case EshopActions.AddProductImage:
      {
        return { ...state,
          productImages: action.payload.all
        };
      }

    case EshopActions.AddProductImagesUrlSuccess:
      {
        return { ...state,
          productImages: action.payload.all
        };
      }

    case EshopActions.RemoveProductImageSuccess:
      {
        return { ...state,
          productImages: action.payload.all
        };
      }

    case EshopActions.GetAllTranslationsSuccess:
      {
        return { ...state,
          translations: action.payload
        };
      }

    case EshopActions.GetAllProductsSuccess:
      {
        return { ...state,
          allProducts: action.payload
        };
      }

    case EshopActions.GetAllCategoriesSuccess:
      {
        return { ...state,
          allCategories: action.payload
        };
      }

    case EshopActions.EditTranslationSuccess:
      {
        return { ...state,
          translations: state.translations.map(trans => trans._id === action.payload._id ? action.payload : trans)
        };
      }

    default:
      {
        return state;
      }
  }
}
export const orders = state => state.orders;
export const order = state => state.order;
export const productImages = state => state.productImages;
export const translations = state => state.translations;
export const allProducts = state => state.allProducts;
export const allCategories = state => state.allCategories;
