import { EshopActions } from '../actions';
export const initialState = {
  products: null,
  loadingProducts: false,
  categories: [],
  pagination: {
    page: 1,
    pages: 1,
    total: 0
  },
  product: null,
  loadingProduct: false,
  cart: null,
  userOrders: null,
  order: null,
  productsTitles: [],
  priceFilter: Infinity,
  maxPrice: Infinity,
  minPrice: 0,
  position: null,
  loading: false,
  error: ''
};
export function productReducer(state = initialState, action) {
  switch (action.type) {
    case EshopActions.GetProducts:
      {
        return { ...state,
          loadingProducts: true
        };
      }

    case EshopActions.GetProductsSuccess:
      {
        return { ...state,
          products: action.payload.products,
          pagination: action.payload.pagination,
          maxPrice: action.payload.maxPrice,
          minPrice: action.payload.minPrice,
          loadingProducts: false
        };
      }

    case EshopActions.GetCategoriesSuccess:
      {
        return { ...state,
          categories: action.payload
        };
      }

    case EshopActions.GetProduct:
      {
        return { ...state,
          loadingProduct: true
        };
      }

    case EshopActions.GetProductSuccess:
      {
        return { ...state,
          product: action.payload,
          loadingProduct: false
        };
      }

    case EshopActions.GetProductsSearchSuccess:
      {
        return { ...state,
          productsTitles: action.payload
        };
      }

    case EshopActions.GetCartSuccess:
      {
        return { ...state,
          cart: action.payload
        };
      }

    case EshopActions.AddToCartSuccess:
      {
        return { ...state,
          cart: action.payload
        };
      }

    case EshopActions.MakeOrderWithPayment:
      return { ...state,
        loading: true
      };

    case EshopActions.MakeOrderWithPaymentSuccess:
      return { ...state,
        order: action.payload.result,
        cart: action.payload.cart,
        error: action.payload.error,
        loading: false
      };

    case EshopActions.MakeOrderWithPaymentFail:
      return { ...state,
        order: null,
        error: 'ORDER_SUBMIT_ERROR',
        loading: false
      };

    case EshopActions.MakeOrder:
      return { ...state,
        loading: true
      };

    case EshopActions.MakeOrderSuccess:
      return { ...state,
        order: action.payload.result,
        cart: action.payload.cart,
        error: action.payload.error,
        loading: false
      };

    case EshopActions.MakeOrderFail:
      return { ...state,
        order: null,
        error: 'ORDER_SUBMIT_ERROR',
        loading: false
      };

    case EshopActions.GetUserOrdersSuccess:
      {
        return { ...state,
          userOrders: action.payload
        };
      }

    case EshopActions.FilterPrice:
      return { ...state,
        priceFilter: action.payload
      };

    case EshopActions.UpdatePosition:
      {
        return { ...state,
          position: action.payload
        };
      }

    case EshopActions.CleanError:
      {
        return { ...state,
          order: null,
          error: ''
        };
      }

    default:
      {
        return state;
      }
  }
}
export const products = state => state.products;
export const loadingProducts = state => state.loadingProducts;
export const categories = state => state.categories;
export const pagination = state => state.pagination;
export const product = state => state.product;
export const cart = state => state.cart;
export const productLoading = state => state.loadingProduct;
export const userOrders = state => state.userOrders;
export const order = state => state.order;
export const productsTitles = state => state.productsTitles;
export const priceFilter = state => state.priceFilter;
export const maxPrice = state => state.maxPrice;
export const minPrice = state => state.minPrice;
export const position = state => state.position;
