import { EshopActions } from '../actions';
export const initialState = {
  loading: false,
  error: '',
  pages: null,
  page: null,
  themes: null,
  configs: null
};
export function eshopReducer(state = initialState, action) {
  switch (action.type) {
    case EshopActions.GetPagesSuccess:
      {
        return { ...state,
          pages: action.payload
        };
      }

    case EshopActions.GetPageSuccess:
      {
        return { ...state,
          page: action.payload
        };
      }

    case EshopActions.GetThemesSuccess:
      {
        return { ...state,
          themes: action.payload
        };
      }

    case EshopActions.GetConfigsSuccess:
      {
        return { ...state,
          configs: action.payload
        };
      }

    case EshopActions.SendContact:
      {
        return { ...state,
          loading: true
        };
      }

    case EshopActions.SendContactSuccess:
      {
        return { ...state,
          loading: false,
          error: ''
        };
      }

    case EshopActions.SendContactFail:
      {
        return { ...state,
          loading: false,
          error: 'CONTACT_SEND_ERROR'
        };
      }

    default:
      {
        return state;
      }
  }
}
export const loading = state => state.loading;
export const error = state => state.error;
export const pages = state => state.pages;
export const page = state => state.page;
export const themes = state => state.themes;
export const configs = state => state.configs;
