import { EshopActions } from '../actions';
import { languages, currencyLang } from '../../shared/constants';
export const initialState = {
  loading: false,
  user: null,
  lang: languages[0],
  currency: currencyLang['default']
};
export function appReducer(state = initialState, action) {
  switch (action.type) {
    case EshopActions.GetUser:
      {
        return { ...state,
          loading: true
        };
      }

    case EshopActions.StoreUser:
      {
        return { ...state,
          user: action.payload,
          loading: false
        };
      }

    case EshopActions.GetUserFail:
      {
        return { ...state,
          user: action.payload,
          loading: false
        };
      }

    case EshopActions.SignIn:
      {
        return { ...state,
          loading: true
        };
      }

    case EshopActions.SignInSuccess:
      {
        return { ...state,
          user: action.payload,
          loading: false
        };
      }

    case EshopActions.ChangeLanguage:
      {
        return { ...state,
          lang: action.payload.lang,
          currency: action.payload.currency
        };
      }

    default:
      {
        return state;
      }
  }
}
export const user = state => state.user;
export const lang = state => state.lang;
export const currency = state => state.currency;
export const loading = state => state.loading;
